import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { Button, Form } from 'semantic-ui-react'

export default function HalfElfModal() {
    const dispatch = useDispatch()
    const history = useHistory()
    const [numberSelected, setNumberSelected] = useState(0)
    const [statObject, setStatObject] = useState({})
    function handleClick(){
        if(numberSelected !== 2){
            alert("You must choose exactly 2 attributes!")
        }
        else{
            dispatch
        }
    }

    handleChange
    return (
        <Form>

        <Form.Group grouped>
      <label>Choose 2 bonus attributes:</label>
      <Form.Field label='Strength'  control='input' type='checkbox' onChange={(e)=> setStatObject(('stregnth')[e.target.checked])}/>
      <Form.Field label='Dexterity' control='input' type='checkbox' onChange={(e)=> setStatObject(('dexterity')[e.target.checked])}/>
      <Form.Field label='Constitution' control='input' type='checkbox' onChange={(e)=> setStatObject(('constitution')[e.target.checked])}/>
      <Form.Field label='Wisdom' control='input' type='checkbox' onChange={(e)=> setStatObject(('wisdom')[e.target.checked])}/>
      <Form.Field label='Intelligence' control='input' type='checkbox' onChange={(e)=> setStatObject(('intelligence')[e.target.checked])}/>
      <Form.Field label='Charisma' control='input' type='checkbox'  onChange={(e)=> setStatObject(('charisma')[e.target.checked])}/>
      <Button type='submit' color='red' onClick={()=> handleClick()}>Submit</Button>
    </Form.Group>
        </Form>
    )
}
