export const SET_RACE = 'SET_RACE'
export const SET_CLASS = 'SET_CLASS'
export const SET_NAME = 'SET_NAME'



export function setRace(race) {
    return {
        type: SET_RACE,
        payload: {
            race
        }
    }
}

export function setClass(classSelection) {
    return {
        type: SET_CLASS,
        payload: {
            classSelection
        }
    }
}

export function setName(name) {
    return {
        type: SET_NAME,
        payload: {
            name
        }
    }
}
