import React from 'react'
import { Grid } from 'semantic-ui-react'

export default function CharacterStats(props) {
  function modCalc(stat){
    if(stat < 10){
      return "-1"
    }
    else if(stat < 12){
      return '0'
    }
    else{
      return '+' + Math.floor((stat - 10)/2)
    }
  }

  return (
    <Grid.Row>
      <Grid.Column>
        <div>
          Strength: {props.stats[0]}<br/>Strength Mod: {modCalc(props.stats[0])}
        </div>
        <div>
          Dexterity: {props.stats[1]}<br/>Dexterity Mod: {modCalc(props.stats[1])}
        </div>
      </Grid.Column>
    </Grid.Row>
  )
}
