export const SET_RACE = 'SET_RACE'
export const SET_CLASS = 'SET_CLASS'
export const SET_NAME = 'SET_NAME'
export const SET_HALFELFBONUSSTATS = 'SET_HALFELFBONUSSTATS'
export const SET_STRCALCULATOR = 'SET_STRCALCULATOR' 
export const SET_DEXCALCULATOR = 'SET_DEXCALCULATOR' 
export const SET_CONCALCULATOR = 'SET_CONCALCULATOR' 
export const SET_WISCALCULATOR = 'SET_WISCALCULATOR' 
export const SET_INTCALCULATOR = 'SET_INTCALCULATOR' 
export const SET_CHACALCULATOR = 'SET_CHACALCULATOR' 
export const SET_STATCALC = 'SET_STATCALC'



export function setRace(race) {
    return {
        type: SET_RACE,
        payload: {
            race
        }
    }
}

export function setClass(classSelection='') {
    return {
        type: SET_CLASS,
        payload: {
            classSelection 
        }
    }
}

export function setName(name) {
    return {
        type: SET_NAME,
        payload: {
            name
        }
    }
}

export function setHalfElfBonusStats(halfElfBonusStats) {
    return {
        type: SET_HALFELFBONUSSTATS,
        payload: {
            halfElfBonusStats
        }
    }
}

export function setStatCalc(statName, pointValue){
    return {
        type: SET_STATCALC,
        payload: {
            statName,
            pointValue
        }
    }
}

export function setStrCalculator(pointCalculator) {
    return {
        type: SET_STRCALCULATOR,
        payload: {
            pointCalculator
        }
    }
}
export function setDexCalculator(pointCalculator) {
    return {    
        type: SET_DEXCALCULATOR,
        payload: {
            pointCalculator
        }
    }
}
export function setConCalculator(pointCalculator) {
    return {
        type: SET_CONCALCULATOR,
        payload: {
            pointCalculator
        }
    }
}
export function setWisCalculator(pointCalculator) {
    return {
        type: SET_WISCALCULATOR,
        payload: {
            pointCalculator
        }
    }
}
export function setIntCalculator(pointCalculator) {
    return {
        type: SET_INTCALCULATOR,
        payload: {
            pointCalculator
        }
    }
}
export function setChaCalculator(pointCalculator) {
    return {
        type: SET_CHACALCULATOR,
        payload: {
            pointCalculator
        }
    }
}
