import React from 'react'
import { Segment } from 'semantic-ui-react'

export default function Character(props) {
  return (
    <Segment inverted style={{marginTop:"20px", width:'60vw'}}>
      <Segment textAlign='left' color='red' inverted style={{marginBottom:"0px", borderBottomLeftRadius:'0px', borderBottomRightRadius:'0px', padding:'4px', borderBottom:'2px solid dimgray'}}>
        Character: {props.character.name}
      </Segment>
      <Segment textAlign='left' color='red' inverted style={{marginTop:"0px", marginBottom:'0px', borderRadius:'0px', padding:'4px'}}>
        Race: {props.character.race.charAt(0).toUpperCase()}{props.character.race.slice(1)}
      </Segment>
      <Segment textAlign='left' color='red' inverted style={{marginTop:"0px", borderTopLeftRadius:'0px', borderTopRightRadius:'0px', padding:'4px'}}>
        Character: {props.character.class.charAt(0).toUpperCase()}{props.character.class.slice(1)}
      </Segment>
    </Segment>
  )
}
