import { combineReducers } from "redux";

const {  SET_RACE, SET_CLASS, SET_NAME } = require("./actions");



function raceReducer(state='', action) {
    switch (action.type) {
        case SET_RACE:
            return action.payload.race



            default:
            return state;
    }
}

function classReducer(state='', action) {
    switch (action.type) {
        case SET_CLASS:
            return action.payload.classSelection



            default:
            return state;
    }
}

function nameReducer(state='', action) {
    switch (action.type) {
        case SET_NAME:
            return action.payload.name



            default:
            return state;
    }
}

export const rootReducer = combineReducers({
    race: raceReducer,
    classSelection: classReducer,
    name: nameReducer
})
