import React from 'react'
import { Grid, Segment } from 'semantic-ui-react'

export default function CharacterStats(props) {
  const circle = { width: 35, height: 35, fontSize:'10px', padding:'6px', margin:'auto', marginTop:'10px', border:'lightgray solid 1px', borderRadius:'50px' }
  function modCalc(stat){
    if(stat < 10){
      return "-1"
    }
    else if(stat < 12){
      return '0'
    }
    else{
      return '+' + Math.floor((stat - 10)/2)
    }
  }

  return (

    <Grid celled>
      <Grid.Column width={2}>
        <Segment>
        Strength: {props.stats[0]}<br/><Segment style={circle}>{modCalc(props.stats[0])}</Segment>
        </Segment>
        <Segment>
        Dexterity: {props.stats[1]}<br/><Segment style={circle}>{modCalc(props.stats[1])}</Segment>
        </Segment>
        <Segment>
        Constitution: {props.stats[2]}<br/><Segment style={circle}>{modCalc(props.stats[2])}</Segment>
        </Segment>
      </Grid.Column>

    <Grid.Column width={2}>
      <Segment>
      Wisdom<br/>{props.stats[3]}<br/><Segment style={circle}>{modCalc(props.stats[3])}</Segment>
      </Segment>
      <Segment>
      Intelligence: {props.stats[4]}<br/><Segment style={circle}>{modCalc(props.stats[4])}</Segment>
      </Segment>
      <Segment>
      Charisma: {props.stats[5]}<br/><Segment style={circle}>{modCalc(props.stats[5])}</Segment>
      </Segment>
    </Grid.Column>
    </Grid>      

  )
}
