import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { Button, Form } from 'semantic-ui-react'

export default function HalfElfModal() {
    const dispatch = useDispatch()
    const history = useHistory()
    const [numberSelected, setNumberSelected] = useState(0)
    const [statObject, setStatObject] = useState({stats : {"strenth":false, 'dexterity':false, 'constitution':false, 'wisdom':false,'intelligence':false, 'charisma':false}})

    function handleClick() {
        return
    }

    function updateStatObject(stat, e) {
        let newObject = {...statObject.stats}
        newObject[stat] = true
        return { newObject }
    }

    return (<Form>
        <Form.Group grouped>
      <label>Choose 2 bonus attributes:</label>
      <Form.Field label='Strength'  control='input' type='checkbox' onChange={(e)=> setStatObject(updateStatObject('strength', e.target.checked)) }/>
      <Form.Field label='Dexterity' control='input' type='checkbox' onChange={(e)=> setStatObject({'dexterity' : e.target.checked})}/>
      <Form.Field label='Constitution' control='input' type='checkbox' onChange={(e)=> setStatObject(statObject['constitution'] = e.target.checked)}/>
      <Form.Field label='Wisdom' control='input' type='checkbox' onChange={(e)=> setStatObject(statObject['wisdom'] = e.target.checked)}/>
      <Form.Field label='Intelligence' control='input' type='checkbox' onChange={(e)=> setStatObject(statObject['intelligence'] = e.target.checked)}/>
      <Form.Field label='Charisma' control='input' type='checkbox'  onChange={(e)=> setStatObject(statObject['charisma'] = e.target.checked)}/>
      <Button type='submit' color='red' onClick={()=> handleClick()}>Submit</Button>
        </Form.Group>
        </Form>
    )
}
