import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Grid } from 'semantic-ui-react'
import PointSpender from './PointSpender'

export default function Attribute(props) {
    const [racialBonus, setRacialBonus] = useState(0)
    const race = useSelector((state)=> state.race)
    const halfElfBonusStats = useSelector((state)=>state.halfElfBonusStats)

    useEffect(()=>{
        if (props.attribute === "Strength" && (race === 'human' || 'str' in halfElfBonusStats)){
            setRacialBonus(1)
        }
        else if (props.attribute === "Dexterity" && (race === 'human' || 'dex' in halfElfBonusStats)){
            setRacialBonus(1)
        }
        else if (props.attribute === "Constitution" && (race === 'human' || 'con' in halfElfBonusStats || race==='half-orc' || race==='gnome')){
            setRacialBonus(1)
        }
        else if (props.attribute === "Wisdom" && (race === 'human' || 'wis' in halfElfBonusStats || race ==='dwarf' || race === 'halfling')){
            setRacialBonus(1)
        }
        else if (props.attribute === "Intelligence" && (race === 'human' || 'int' in halfElfBonusStats || race ==='elf' || race === 'tiefling')){
            setRacialBonus(1)
        }
        else if (props.attribute === "Charisma" && (race === 'human' || 'cha' in halfElfBonusStats || race === 'dragonborn')){
            setRacialBonus(1)
        }
        else if (props.attribute === "Strength" && (race === 'half-orc' || race === 'dragonborn')){
            setRacialBonus(2)
        }
        else if (props.attribute === "Dexterity" && (race === 'elf' || race === 'halfling')){
            setRacialBonus(2)
        }
        else if (props.attribute === "Constitution" && race === 'dwarf'){
            setRacialBonus(2)
        }
        else if (props.attribute === "Intelligence" && (race === 'gnome' )){
            setRacialBonus(2)
        }
        else if (props.attribute === "Charisma" && (race === 'tiefling' || race === 'half-elf')){
            setRacialBonus(2)
        }

    }, [race, props])

    return (

            <Grid.Column>
               <PointSpender racialBonus={racialBonus} name={props.attribute} />
    <div>Racial Bonus: {racialBonus}</div>
            </Grid.Column>



    )
}
