export const ADD_RACE = 'ADD_RACE'
export const SET_RACES = 'SET_RACES'

export function addRace(race) {
    return {
        type: ADD_RACE,
        payload: {
            race
        }
    }
}

export function setRaces(racesArray) {
    return {
        type: SET_RACES,
        payload: {
            racesArray
        }
    }
}
