import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Container, Header } from 'semantic-ui-react'

export default function RacialTraits(props) {
  const [traits, setTraits] = useState([])
  const [proficiencies, setProficencies] = useState([])
  const race = useSelector(state=>state.race)

  useEffect(() => {
    fetch(`https://www.dnd5eapi.co/api/races/${props.race}`)
      .then(res => res.json())
      .then(data => {
        setProficencies(data.starting_proficiencies)
        setTraits(data.traits)
      }
    )
  })

  return (
    <>
    <Header>
      Racial Traits
    </Header>
    <Container>
      Traits: {traits.map(trait => {
          return trait.name + " ♦ "
        })}
    </Container>
    <Container>
      Proficencies: {proficiencies.map(proficiency => {
          return proficiency.name + " ♦ "
        })}
    </Container>
    </>
  )
}
