import React, { useEffect, useState } from 'react'
// import { useDispatch } from 'react-redux'
import { Button, Card, Grid, GridColumn, Image, Segment } from 'semantic-ui-react'
// import { setRaces } from '../../redux/actions'
import 'semantic-ui-css/semantic.min.css'
// import halfOrc from './racesImages/half-orc.jpg'
import dwarf from './classImages/dwarf.jpg'



export default function ClassCard(props) {
    const [classData, setClassData] = useState([])
    const [classFeatures, setClassFeatures] = useState([])
    const [loaded, setLoaded] = useState([false])
    // dispatch = useDispatch()
    useEffect(() => {
        fetch(`https://www.dnd5eapi.co/api/classes/${props.classNames}`)
          .then(res => res.json())
          .then(data => {
            setClassData(data);
            const proficienciesList = classData.proficiencies
          })
          fetch(`https://www.dnd5eapi.co/api/classes/${props.classNames}/levels`)
          .then(res => res.json())
          .then(data => {
            setClassFeatures(data);
            setLoaded(true)
          })
      }, [props])



    return (

            <Grid celled>


            <Grid.Row> 
                <Grid.Column width={4}>
                    <Image src={dwarf} alternate='' fluid ui={false} />
                </Grid.Column> 

            <Grid.Column width={12}>

                <h2 style={{textAlign:"left",  borderBottom:"1px solid black"}}>
                    {classData.name}<Button color='red' style={{marginLeft:"10px"}}>+select class</Button>
                </h2>
                {loaded === true && <><p style={{textAlign:"left", fontSize:"20px"}}>
                Proficencies: {proficienciesList.map(proficiency => {
                   return proficiency.name})}
                </p>
                <p style={{textAlign:"left", fontSize:"18px"}}>
                    Features: {classFeatures.features.map(feature => {
                        return feature.name
                    })}
                </p>

                    {classData.hasAttribute('spellcasting') && <p style={{textAlign:"left", fontSize:"18px"}}>{classData.spellcasting.info.map(description => {
                        return description.name + ": " + description.desc
                    })} </p>}</>}
            </Grid.Column>
            </Grid.Row> 
            </Grid>

    )
}
