import React, { useEffect, useState } from 'react'
import { Container, Header } from 'semantic-ui-react'

export default function Equipment(props) {
  const [equipment, setEquipment ] = useState([])
  const [startingEquipment, setStartingEquipment] = useState([])


  useEffect(() => {
    fetch(`https://www.dnd5eapi.co/api/starting-equipment/${props.class}`)
    .then(res => res.json())
    .then(data => {
        setEquipment(data.starting_equipment)
        setStartingEquipment(data.starting_equipment_options)
    })
  }, [props])

  return (
    <>
    <Header>
      Equipment:
    </Header>
    <Container>
      {equipment.map(item => (item.name + '♦'))}
    </Container>
    </>
  )
}
