import { combineReducers } from "redux";

const {  SET_RACE, SET_CLASS } = require("./actions");



function raceReducer(state='', action) {
    switch (action.type) {
        case SET_RACE:
            return action.payload.race



            default:
            return state;
    }
}

function classReducer(state='', action) {
    switch (action.type) {
        case SET_CLASS:
            return action.payload.class



            default:
            return state;
    }
}

export const rootReducer = combineReducers({
    race: raceReducer,
    class: classReducer
})
