import React from 'react'
import { Grid, Segment } from 'semantic-ui-react'

export default function CharacterStats(props) {
  function modCalc(stat){
    if(stat < 10){
      return "-1"
    }
    else if(stat < 12){
      return '0'
    }
    else{
      return '+' + Math.floor((stat - 10)/2)
    }
  }

  return (
    <>

      <Grid.Column width={1}>
        <Segment>
          Strength: {props.stats[0]}<br/>Str Mod: {modCalc(props.stats[0])}
        </Segment>
        <Segment>
          Dexterity: {props.stats[1]}<br/>Dex Mod: {modCalc(props.stats[1])}
        </Segment>
        <Segment>
          Constitution: {props.stats[2]}<br/>Con Mod: {modCalc(props.stats[2])}
        </Segment>
      </Grid.Column>

    <Grid.Column width={2}>
      <Segment>
        Wisdom: {props.stats[3]}<br/>Wis Mod: {modCalc(props.stats[3])}
      </Segment>
      <Segment>
        Intelligence: {props.stats[4]}<br/>Int Mod: {modCalc(props.stats[4])}
      </Segment>
      <Segment>
        Charisma: {props.stats[5]}<br/>Cha Mod: {modCalc(props.stats[5])}
      </Segment>
    </Grid.Column>
  </>
  )
}
