import React, { useEffect, useState } from 'react'
// import { useDispatch } from 'react-redux'
import { Card, Feed, Image } from 'semantic-ui-react'
// import { setRaces } from '../../redux/actions'
import 'semantic-ui-css/semantic.min.css'
import halfOrc from './racesImages/half-orc.jpg'
import dwarf from './racesImages/dwarf.jpg'



export default function RaceCard(props) {
    const [raceData, setRaceData] = useState([])
    // dispatch = useDispatch()
    useEffect(() => {
        fetch(`https://www.dnd5eapi.co/api/races/${props.race}`)
          .then(res => res.json())
          .then(data => {
            setRaceData(data);
          })
      }, [props])

    //   require(`./racesImages/${raceData.index}.jpg`).default
    return (
        <Card fluid>
            <Card.Content>
                <Feed>
            <Feed.Label image={dwarf} alternate='' wrapped ui={false} />
                <Card.Header>{raceData.name}</Card.Header>
                <Card.Meta>Size: {raceData.size}</Card.Meta>
                <Card.Description>
                    {raceData.alignment}
                </Card.Description> 
                </Feed>
            </Card.Content>
        </Card>
    )
}
