import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import { Grid } from 'semantic-ui-react';
import CharacterStats from './CharacterStats';

export default function CharacterSheet() {
  const [character, setCharacter ] = useState({});
  const [statArray] = useState([character.str, character.dex, character.con, character.wis, character.int, character.cha])
    const { id } = useParams();

    useEffect(() => {
        fetch(`/api/v1/characters/${id}`)
        .then(res => res.json())
        .then(data => {
            setCharacter(data);
        })    
    }, [id])

  return (
    <div>
      {character.name} {character.class} {character.race}
      <Grid>
        <CharacterStats stats={statArray} />
      </Grid>
    </div>
  )
}
