import { combineReducers } from "redux";

const {  SET_RACE, SET_CLASS, SET_NAME, SET_HALFELFBONUSSTATS, SET_POINTCALCULATOR, SET_CONCALCULATOR, SET_DEXCALCULATOR, SET_STRCALCULATOR, SET_WISCALCULATOR, SET_INTCALCULATOR, SET_CHACALCULATOR, SET_STATCALC, SET_STR, SET_DEX, SET_WIS, SET_CON, SET_INT, SET_CHA } = require("./actions");



function raceReducer(state='', action) {
    switch (action.type) {
        case SET_RACE:
            return action.payload.race



            default:
            return state;
    }
}

function halfElfBonusStatsReducer(state=[], action){
    switch (action.type) {
        case SET_HALFELFBONUSSTATS:
            return state.concat(action.payload.halfElfBonusStats)

        default:
            return state;
    }
}

function classReducer(state='', action) {
    switch (action.type) {
        case SET_CLASS:
            return action.payload.classSelection



            default:
            return state;
    }
}

function nameReducer(state='', action) {
    switch (action.type) {
        case SET_NAME:
            return action.payload.name



            default:
            return state;
    }
}

function strReducer(state=8, action) {
    switch (action.type) {
        case SET_STR:
            return action.payload.str



            default:
            return state;
    }
}

function dexReducer(state=8, action) {
    switch (action.type) {
        case SET_DEX:
            return action.payload.dex



            default:
            return state;
    }
}

function conReducer(state=8, action) {
    switch (action.type) {
        case SET_CON:
            return action.payload.con



            default:
            return state;
    }
}

function wisReducer(state=8, action) {
    switch (action.type) {
        case SET_WIS:
            return action.payload.wis



            default:
            return state;
    }
}

function intReducer(state=8, action) {
    switch (action.type) {
        case SET_INT:
            return action.payload.int



            default:
            return state;
    }
}

function chaReducer(state=8, action) {
    switch (action.type) {
        case SET_CHA:
            return action.payload.cha



            default:
            return state;
    }
}

function pointCalculatorReducer(state={"Strength":[0,8],"Dexterity":[0,8],"Constitution":[0,8],"Wisdom":[0,8],"Intelligence":[0,8],"Charisma":[0,8]}, action) {
    switch (action.type) {
        case SET_STATCALC:
            return {...state, [action.payload.statName]:action.payload.pointValue}

            default:
            return state;
    }
}

export const rootReducer = combineReducers({
    race: raceReducer,
    classSelection: classReducer,
    name: nameReducer,
    halfElfBonusStats: halfElfBonusStatsReducer,
    pointCalculator: pointCalculatorReducer,
    str: strReducer,
    dex: dexReducer,
    con: conReducer,
    wis: wisReducer,
    int: intReducer,
    cha: chaReducer
})
