import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { Button, Form } from 'semantic-ui-react'

export default function HalfElfModal() {
    const dispatch = useDispatch()
    const history = useHistory()
    const [numberSelected, setNumberSelected] = useState(0)

    return (
        <Form>

        <Form.Group grouped>
      <label>Choose 2 bonus stats:</label>
      <Form.Field label='Strength' control='input' type='checkbox' onChange={(e)=> e.target.checked ? setNumberSelected(numberSelected + 1) : setNumberSelected(numberSelected - 1)}/>
      <Form.Field label='Dexterity' control='input' type='checkbox' onChange={(e)=> e.target.checked ? setNumberSelected(numberSelected + 1) : setNumberSelected(numberSelected - 1)}/>
      <Form.Field label='Constitution' control='input' type='checkbox' onChange={(e)=> e.target.checked ? setNumberSelected(numberSelected + 1) : setNumberSelected(numberSelected - 1)}/>
      <Form.Field label='Wisdom' control='input' type='checkbox' onChange={(e)=> e.target.checked ? setNumberSelected(numberSelected + 1) : setNumberSelected(numberSelected - 1)}/>
      <Form.Field label='Intelligence' control='input' type='checkbox' onChange={(e)=> e.target.checked ? setNumberSelected(numberSelected + 1) : setNumberSelected(numberSelected - 1)}/>
      <Form.Field label='Charisma' control='input' type='checkbox'  onChange={(e)=> e.target.checked ? setNumberSelected(numberSelected + 1) : setNumberSelected(numberSelected - 1)}/>
      <Button type='submit'>Submit</Button>
    </Form.Group>
        </Form>
    )
}
