import { combineReducers } from "redux";

const {  ADD_RACE } = require("./actions");



function racesReducer(state=[], action) {
    switch (action.type) {
        case ADD_RACE:
            return state.concat(action.payload.race)



            default:
            return state;
    }
}

export const rootReducer = combineReducers({
    races: racesReducer
})
