import { combineReducers } from "redux";

const {  SET_RACE, SET_CLASS, SET_NAME, SET_HALFELFBONUSSTATS, SET_POINTCALCULATOR, SET_CONCALCULATOR, SET_DEXCALCULATOR, SET_STRCALCULATOR, SET_WISCALCULATOR, SET_INTCALCULATOR, SET_CHACALCULATOR, SET_STATCALC } = require("./actions");



function raceReducer(state='', action) {
    switch (action.type) {
        case SET_RACE:
            return action.payload.race



            default:
            return state;
    }
}

function halfElfBonusStatsReducer(state=[], action){
    switch (action.type) {
        case SET_HALFELFBONUSSTATS:
            return state.concat(action.payload.halfElfBonusStats)

        default:
            return state;
    }
}

function classReducer(state='', action) {
    switch (action.type) {
        case SET_CLASS:
            return action.payload.classSelection



            default:
            return state;
    }
}

function nameReducer(state='', action) {
    switch (action.type) {
        case SET_NAME:
            return action.payload.name



            default:
            return state;
    }
}

function pointCalculatorReducer(state={"Strength":[0,8],"Dexterity":[0,8],"Constitution":[0,8],"Wisdom":[0,8],"Intelligence":[0,8],"Charisma":[0,8]}, action) {
    switch (action.type) {
        case SET_STATCALC:
            return {...state, [action.payload.statName]:action.payload.pointValue}

        case SET_STRCALCULATOR:
            return { ...state, "Strength" : action.payload.str} 

        case SET_DEXCALCULATOR:
            return { ...state, "Dexterity" : action.payload.dex} 

        case SET_CONCALCULATOR:
            return { ...state, "Constitution" : action.payload.con} 

        case SET_WISCALCULATOR:
            return { ...state, "Wisdom" : action.payload.wis} 

        case SET_INTCALCULATOR:
            return { ...state, "Intelligence" : action.payload.int} 

        case SET_CHACALCULATOR:
            return { ...state, "Charisma" : action.payload.cha} 

            default:
            return state;
    }
}

export const rootReducer = combineReducers({
    race: raceReducer,
    classSelection: classReducer,
    name: nameReducer,
    halfElfBonusStats: halfElfBonusStatsReducer,
    pointCalculator: pointCalculatorReducer
})
